function initialize(){var e=document.getElementById("exit-data"),t=e.getAttribute("data-lat-id"),a=e.getAttribute("data-lon-id"),n=Number(e.getAttribute("data-heading-id")),i=new google.maps.LatLng(t,a),o={center:i,zoom:18},g=new google.maps.Map(document.getElementById("map-canvas"),o),d={position:i,pov:{heading:n,pitch:10}},m=new google.maps.StreetViewPanorama(document.getElementById("pano"),d);g.setStreetView(m)}google.maps.event.addDomListener(window,"page:change",initialize);